package app.vercel.cardly.domain.user;import lombok.*;import javax.persistence.*;@Entity@Table(name = "user")@Getter@NoArgsConstructor(access = AccessLevel.PROTECTED)@AllArgsConstructor@Builderpublic class User {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(nullable = false, unique = true, length = 100)    private String email;    @Column(nullable = false, length = 255)    private String password;    @Column(nullable = false, length = 50)    private String name;    public boolean isCorrectPassword(String rawPassword, org.springframework.security.crypto.password.PasswordEncoder encoder) {        return encoder.matches(rawPassword, this.password);    }}